from django.contrib import admin
from .models import Judet, TipLocalitate, Localitate, Artera, CodPostal, RelatiiLocalitati


@admin.register(Judet)
class JudetAdmin(admin.ModelAdmin):
    list_display = ["cod", "nume"]
    search_fields = ["nume"]
    ordering = ["nume"]


@admin.register(TipLocalitate)
class TipLocalitateAdmin(admin.ModelAdmin):
    list_display = ["cod", "denumire"]
    search_fields = ["denumire"]
    ordering = ["cod"]


@admin.register(Localitate)
class LocalitateAdmin(admin.ModelAdmin):
    list_display = ["cod", "denumire", "judet", "cod_postal"]
    search_fields = ["denumire", "cod"]
    list_filter = ["judet", "tip_localitate"]
    ordering = ["denumire"]


@admin.register(Artera)
class ArteraAdmin(admin.ModelAdmin):
    list_display = ["cod", "denumire", "loc_cod", "cod_postal", "stare", "tip_artera"]
    search_fields = ["denumire", "cod"]
    list_filter = ["stare", "tip_artera"]
    ordering = ["denumire"]


@admin.register(CodPostal)
class CodPostalAdmin(admin.ModelAdmin):
    list_display = ["cod", "bloc", "cod_postal", "localitate"]
    search_fields = ["bloc", "cod_postal"]
    list_filter = ["localitate"]
    ordering = ["cod_postal"]

